from flask import Flask, render_template
import random

app = Flask(__name__)

images = [
    "https://res.cloudinary.com/highereducation/images/w_1024,h_576,c_scale/f_auto,q_auto/v1670015316/BestColleges.com/closeup-of-python-code/closeup-of-python-code-1024x576.jpg",
    "https://img.freepik.com/premium-vector/programming-code-icon-made-with-binary-code-coding-hacker-matrix-background-with-digits-1-0_127544-1141.jpg?w=2000",
    "https://www.freecodecamp.org/news/content/images/2022/12/main-image.png",
    "https://venturebeat.com/wp-content/uploads/2021/05/GettyImages-1291886933-e1624308433688.jpg?fit=2309%2C1154&strip=all",
    "https://www.herzing.edu/sites/default/files/2020-09/it_computer_programming.jpg",
    "https://www.mooc.org/hubfs/applications-of-computer-programming.jpg",
    "data:image/jpeg;base64,"
]

@app.route('/')
def index():
    url = random.choice(images)
    return render_template('index.html', url=url)

if __name__ == "__main__":
    app.run(host="0.0.0.0")